package addMinus;

public class Math1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int x = 1;
     int y = 2;
     int sum;
     int minus;
     int multiple;
     int divide;
     sum = x + y;
     minus = y - x;
     multiple = x * y;
     divide = y / x;
     System.out.println("The sum of the variables is " + sum + ".");
     System.out.println("The subtraction of the variables is " + minus + ".");
     System.out.println("The multiplication of the variables is " + multiple + ".");
     System.out.println("The division of the variables is " + divide + ".");
	
	} 

}
