package NumericalData;

public class eNotation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("865000000.0"); 
		System.out.printf("%.1f%n",8.65e8); 
		//865000000.0can be written as 8.65e8
		System.out.println("0.000483"); 
		System.out.printf("%f%n",4.83e-4); 
		System.out.printf("%.6f%n",4.83e-4);
		//0.000483 can be written as 4.83e-4

	}

}
