package SelfProgramming;

public class BooleanExpressions {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        //DECLARE
		int x = 1;
        int y = 2;
        int total = x + y;
		
		
		//EQUAL TO
		System.out.println(total == 3);
		//NOT EQUAL TO
		System.out.println(total != 3);
		//LESS THAN
		System.out.println(total < 3);
		//GREATER THAN
		System.out.println(total > 3);
		//LESS THAN OR EQUAL TO
		System.out.println(total <= 3);
		//MORE THAN OR EQUAL TO
		System.out.println(total >= 3);
	
			
	}

}
