package SelfProgramming;

public class Example12 {
// FLAG CONTROLLED LOOP
// USE BOOLEAN VALUE TO CONTROL THE LOOP
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean found = false;
		int i = 5;
		while (!found){	
		if(i==5) {
			found = true;
		System.out.println("i = " +i);
		}
		}

	}

}
