package SelfProgramming;

public class Example2 {
//WHILE
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sum    = 0;
		int number = 1;
		  while (number <= 10)
		  {
			sum+=number;
			number = number + 5;
			System.out.println("SUM : " + sum);
		  }
		  System.out.println("Good Bye!");

	}

}
