package SelfProgramming;

public class Example9 {
// INFINITE NESTED FOR LOOP
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int product =0;
		for (product = 0;product < 10;);{
		product = product * 2;
        System.out.println(product);
        }

	}

}
// OUTPUT = 0
//          0
//          0
//          0
//          ...