package myWhile;

public class NoteSlideDoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
		int j=10;
		int k=20;
	    do{
	      System.out.print(i + " ");
	      i++;   
	    }while(i<5);
	    System.out.println();
	    do{
	      System.out.print(j + " ");
	      j++;  //j=j+1
	    }while(j<20);
	    System.out.println();
	    do{
	      System.out.print(k + " ");
	      k--;  //k=k-1
	    } while (k>10);
	
	
	}

}
