package myWhile;

public class NoteSlideWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
		int j=10;
		int k=20;
	    while (i<5){
	      System.out.print(i + " ");
	      i++;   
	    }
	    System.out.println();
	    while (j<20){
	      System.out.print(j + " ");
	      j++;  //j=j+1
	    }
	    System.out.println();
	    while (k>10){
	      System.out.print(k + " ");
	      k--;  //k=k-1
	    }
	
	
	
	
	}

}
