package myWhile;

public class NoteslideFor {
//TERMINATE IF CODING LOOP UNTIL INFINITY
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		int j;
		int k;
	    for (i=1; i<5; i++){
	      System.out.print(i + " ");  
	    }
	    System.out.println();
	    for(j=10; j<20; j++){
	      System.out.print(j + " ");
          //j=j+1
	    }
	    System.out.println();
	  for (k=20; k>10; k--){
	      System.out.print(k + " ");
          //k=k-1
	    }
	}

}
